import React from 'react';
import {connect} from 'react-redux';
import { findRecipe } from '../actions';
import { fetchMenu } from '../actions'


import Menu from './menu';
import Missing from './missing';
import Kitchen from './kitchen';


export class Meals extends React.Component {

    onRecipeSubmit(event) {
        event.preventDefault();
        const choice = document.querySelector('input[name="meals"]:checked').value;
        if (choice){
            console.log(choice + " is selected")
            this.props.dispatch(findRecipe(choice))
            //JUST FOR NOW BELOW
            this.props.dispatch(fetchMenu())

        }
        else{
            console.log("problem in submit")
        }
    }

    // componentDidMount() {
    //     console.log("mounted")
    //     this.props.dispatch(fetchMenu())
    //   }

    render() {
        if (window.innerWidth <= 800){
            return (
            <main>
                <div className="meal-side">
                <form name="choose-recipe" onSubmit={(e) => this.onRecipeSubmit(e)}>
                    <div className="my-meals menu">
                        <Menu />
                    </div>
                    <div className="recipe-info">
                        <Missing 
                            theMissing={this.props.theMissing}/>
                        <button type="submit" name="Recipe" >Get Recipe</button>
                    </div>
                    </form>
                </div>
            </main>
        )}
        else {
            return (
            <Kitchen
                theMissing={this.props.theMissing}
             />
        )}
    } 

}

const mapStateToProps = state => ({
    menu: state.menu
})

export default connect(mapStateToProps)(Meals);
