export const ADD_TO_KITCHEN = 'ADD_TO_KITCHEN';
export const addToKitchen = ingredient => ({
    type: ADD_TO_KITCHEN,
    ingredient
});

export const REMOVE_FROM_KITCHEN = 'REMOVE_FROM_KITCHEN';
export const removeFromKitchen = item => ({
    type: REMOVE_FROM_KITCHEN,
    item
});

export const FIND_RECIPE = 'FIND_RECIPE';
export const findRecipe = choice => ({
    type: FIND_RECIPE,
    choice
});

export const UPDATE_MENU = 'UPDATE_MENU';
export const updateMenu = menu => ({
    type: UPDATE_MENU,
    menu
});

export const fetchMenu = () => dispatch => {
    const recipePuppy = 'https://cors-anywhere.herokuapp.com/http://www.recipepuppy.com/api?i='
    var fullMenu = []
    fetch(recipePuppy + `onions,garlic` + '&p=' + 1)
        .then(res => {
            console.log("fetched! waiting...")
            if (!res.ok) {
                return Promise.reject(res.statusText);
            }
            return res.json();
        })
        .then(menu => {
            fullMenu.push(menu);
        })
        .then(more => {
            fetch(recipePuppy + `onions,garlic` + '&p=' + 2)
            .then(res => {
                console.log("fetched! waiting...")
                if (!res.ok) {
                    return Promise.reject(res.statusText);
                }
                return res.json();
            })
            .then(menu => {
                fullMenu.push(menu);
                dispatch(updateMenu(fullMenu));
            });
        })

}

export const LOG_IN = 'LOG_IN';
export const logIn = () => ({
    type: LOG_IN
});

export const CREATE_USER = 'CREATE_USER';
export const createUser = () => ({
    type: CREATE_USER
});

export const LOG_OUT = 'LOG_OUT';
export const logOut = () => ({
    type: LOG_OUT
});

export const RESIZE = 'RESIZE';
export const resize = () => ({
    type: RESIZE
});
