import React from 'react';

export default function Menu(props) {

  const options = props.options.map((option, index) => (
    <li 
      key={index} 
      className={"meals " + y-{option}}
      onClick={() => {
        document.getElementById(`y-${option}`).classList.add("selected")
      }}
      onMouseOut={() => {
        document.getElementById(`y-${option}`).classList.remove("selected")
      }}>
        <div className="example-picture"></div>
        <p>{option}</p>
    </li>
  ))
  

  return (
    <ul>
      {options}
    </ul>
  );
}
