import React from 'react';
import { connect } from 'react-redux';

import { addToKitchen } from '../actions';
import { fetchMenu } from '../actions';


export class AddingIngredient extends React.Component {

  returnIngredients(){
    return this.props.ingredients
  }
  
  onSubmit(event, returnIngredients) {
    event.preventDefault();
    var text = this.textInput.value.trim()
    var run = this.props
    var blank = this.textInput
    var stuff = this.props.ingredients

    var first = new Promise(this.returnIngredients(), function(resolve){
      console.log(returnIngredients())
      run.dispatch(addToKitchen(text))
      blank.value = ''
      resolve(returnIngredients())
    });
    first.then( returnedIngredients => {
      console.log(returnedIngredients)
      run.dispatch(fetchMenu())
      
    })
  }

  render (){
    return (
      <form className="card add-form" onSubmit={(e) => this.onSubmit(e)}>
        <input 
              type="text" 
              className="ingredient-input" 
              maxLength="25" 
              name="ingredient" 
              placeholder="What else is in your kitchen?"
              autoComplete="off"
              ref={input => this.textInput = input}
              required
          />
          <button 
              type="submit" 
              name="Add"
          >
              Add
          </button>
      </form>
    );
  }
}

const mapStateToProps = state => ({
  options: state.options,
  ingredients: state.ingredients
});

export default connect (mapStateToProps)(AddingIngredient);
