import React from 'react';
import {connect} from 'react-redux';
import { findRecipe } from '../actions';
import { fetchMenu } from '../actions'


import Menu from './menu';
import Missing from './missing';
import Kitchen from './kitchen';


export class Meals extends React.Component {

    checkHref(food, target){
        return food == target
    }
    onRecipeSubmit(event) {
        event.preventDefault();
        const choice = document.querySelector('input[name="meals"]:checked').value;
        var newRecipes = this.props.recipes
        var keys = Object.keys(newRecipes)
        var index = keys.findIndex(checkHref(food, choice))
        console.log(index)

        // var index = peoples.findIndex(p => p.attr1 == "john")
        // var index = newRecipes.findIndex(food => newRecipes[food] === newRecipes[choice])
        // console.log(index)

        if (choice){
            console.log(choice + " is selected")
            // console.log(this.props.recipes)
            // console.log(newPage + " is the href")
            // this.props.dispatch(findRecipe(choice))
            //JUST FOR NOW BELOW
            // this.props.dispatch(fetchMenu())
        }
        else{
            console.log("problem in submit")
        }
    }

    // window.open("https://www.w3schools.com")

    componentDidMount() {
        console.log("mounted")
        this.props.dispatch(fetchMenu())
      }

    render() {
        if (window.innerWidth <= 800){
            return (
            <main>
                <div className="meal-side">
                <form name="choose-recipe" onSubmit={(e) => this.onRecipeSubmit(e)}>
                    <div className="my-meals menu">
                        <Menu />
                    </div>
                    <div className="recipe-info">
                        <Missing 
                            theMissing={this.props.theMissing}/>
                        <button type="submit" name="Recipe" >Get Recipe</button>
                    </div>
                    </form>
                </div>
            </main>
        )}
        else {
            return (
            <Kitchen
                theMissing={this.props.theMissing}
             />
        )}
    } 

}

const mapStateToProps = state => ({
    menu: state.menu,
    recipes: state.recipes
})

export default connect(mapStateToProps)(Meals);
