import React from 'react';

export default function Pantry(props) {
  const ingredients = props.ingredients.map((ingredient, index) => (
    <li 
      key={index} 
      className="ingredients"
      onMouseOver={() => {
        document.getElementById(`x${index}`).classList.remove("invisible")
      }}
      onMouseOut={() => {
        document.getElementById(`x${index}`).classList.add("invisible")
      }}>
      {ingredient}
      <span className={"remove-ingredient invisible"} id={"x" + index}>x</span>
    </li>
  ))
  return (
    <div className="my-kitchen menu">
        <ul>
          {ingredients}
        </ul>
    </div>
  );
}
