import React from 'react';
import { connect } from 'react-redux';

import { addToKitchen } from '../actions';
import { fetchMenu } from '../actions';


export class AddingIngredient extends React.Component {

  onSubmit(event) {
    event.preventDefault();
    var text = this.textInput.value.trim()
    var blank = this.textInput
    var ingreds = this.props.ingredients

    var first = new Promise(function(resolve){
      this.props.dispatch(addToKitchen(text))
      blank.value = ''
      console.log("first")
      // console.log(stuff)
      resolve()
    });
    first.then(function(){
      console.log("second")
      console.log(ingreds)
      this.props.dispatch(fetchMenu(ingreds))
    })
  }

  render (){
    return (
      <form className="card add-form" onSubmit={(e) => this.onSubmit(e)}>
        <input 
              type="text" 
              className="ingredient-input" 
              maxLength="25" 
              name="ingredient" 
              placeholder="What else is in your kitchen?"
              autoComplete="off"
              ref={input => this.textInput = input}
              required
          />
          <button 
              type="submit" 
              name="Add"
          >
              Add
          </button>
      </form>
    );
  }
}

const mapStateToProps = state => ({
  options: state.options,
  ingredients: state.ingredients
});

export default connect (mapStateToProps)(AddingIngredient);
