import React from 'react'
import ReactDOM from 'react-dom'
import {
  Route,
  NavLink,
  BrowserRouter as Router,
  Switch,
} from 'react-router-dom'
import LoggedIn from './loggedin';
import LoggedOut from './loggedout';
import Pantry from './pantry';
import AddingIngredient from './addingredient';
import Menu from './menu';
import Missing from './missing';


export default class ProfilePage extends React.Component {
    constructor(props) {
        super(props);
    
        this.state = {
            username: '',
        };
    }
  
    render() {   
        return (
            <main>
                <div className="kitchen-side">
                    <Pantry 
                        ingredients={this.state.ingredients} 
                        remove={this.handleRemove}
                    />
                    <AddingIngredient 
                        ingredients={this.state.ingredients} 
                        add={this.handleAdd}
                    />
                </div>
                <div className="meal-side">
                    <div className="my-meals menu">
                        <Menu />
                    </div>
                    <div className="recipe-info">
                        <Missing />
                        <button type="button" name="Recipe" href="">Get Recipe</button>
                    </div>
                </div>
                <div className="profile-page">
                    <NavLink to="/kitchen" id="close-profile">x</NavLink>{' '}
                    <LoggedIn />
                    <LoggedOut />
                </div>
            </main>
        );
    }
}
