import React from 'react';
// import {connect} from 'react-redux';

import Menu from './menu';
import Missing from './missing';
import Kitchen from './kitchen'

export default class Meals extends React.Component {
    constructor(props) {
        super(props);
    
        this.state = {
            ingredients: ["eggs", "milk", "bread", "brown sugar", "cantelope"]
        };
        this.handleRemove = this.handleRemove.bind(this)
        this.handleAdd = this.handleAdd.bind(this)
    }

    handleRemove(item){
        console.log(item)
        var array = [...this.state.ingredients]
        var index = this.state.ingredients.indexOf(item)
        array.splice(index, 1);
        this.setState({ingredients: array});
    }

    handleAdd(item){
        console.log(item)
        var array = [...this.state.ingredients]
        array.push(item);
        this.setState({ingredients: array})
    }

    render() {
        if (window.innerWidth <= 800){
            console.log("small enough")   
        return (
            <main>
                <div className="meal-side">
                    <div className="my-meals menu">
                        <Menu />
                    </div>
                    <div className="recipe-info">
                        <Missing />
                        <button type="button" name="Recipe" href="">Get Recipe</button>
                    </div>
                </div>
            </main>
        )}
        else {
        return (
            <Kitchen />
        )}
    } 

}
