import {ADD_TO_KITCHEN, REMOVE_FROM_KITCHEN, SELECT_MEAL, FIND_RECIPE, UPDATE_MENU, LOG_IN, CREATE_USER, LOG_OUT, RESIZE} from './actions';

const initialState = {
    window: 800,
    username: false,
    ingredients: ["eggs", "milk", "bread"],
    options: [],
    selected: ''
};

export default (state = initialState, action) => {
    if (action.type === LOG_OUT) {
        return Object.assign({}, state, {
            window: 800,
            username: false,
            ingredients: ["eggs", "milk", "bread"],
            options: [],
            selected: ''
        });
    }



}
